$(document).ready(function(){var i=$("#isotope");i.isotope({layoutMode:"fitRows",itemSelector:".writing"}),i.imagesLoaded(function(){i.isotope("layout")}),$(".writing-nav--all").click(function(){i.isotope({filter:"*"})}),$(".writing-nav--comics").click(function(){i.isotope({filter:".comic"})}),$(".writing-nav--articles").click(function(){i.isotope({filter:".article"})}),$(".writing-nav--ecards").click(function(){i.isotope({filter:".ecard"})})});